// color design tokens export
export const colorTokens = {
    grey: {
      0: "#FFFFFF",    // Branco
      10: "#F5F7FA",   // Cinza Claro
      50: "#E4E7EB",   // Cinza
      100: "#CED4D9",  // Cinza Claro
      200: "#A7B6C2",  // Azul Cinza
      300: "#7D97A4",  // Azul Cinza
      400: "#526E8E",  // Azul
      500: "#334E68",  // Azul Escuro
      600: "#243B53",  // Azul Escuro
      700: "#172B3A",  // Azul Marinho
      800: "#0C1C28",  // Azul Marinho
      900: "#020709",  // Preto
      1000: "#000000", // Preto Total
    },
    primary: {
      50: "#f5e7e5",   // Bege Claro
100: "#e0cbc8",  // Bege
200: "#cbaea9",  // Bege Escuro
300: "#b69287",  // Marrom Claro
400: "#9f7568",  // Marrom
500: "#8a5949",  // Marrom Escuro
600: "#744c3b",  // Marrom Profundo
700: "#5e3f2c",  // Marrom Profundo
800: "#49321e",  // Marrom Profundo
900: "#331511",  // Marrom Profundo

    },
  };
  
  // mui theme settings
  export const themeSettings = (mode) => {
    return {
      palette: {
        mode: mode,
        ...(mode === "dark"
          ? {
              // palette values for dark mode
              primary: {
                dark: colorTokens.primary[200],
                main: colorTokens.primary[500],
                light: colorTokens.primary[800],
              },
              neutral: {
                dark: colorTokens.grey[100],
                main: colorTokens.grey[200],
                mediumMain: colorTokens.grey[300],
                medium: colorTokens.grey[400],
                light: colorTokens.grey[700],
              },
              background: {
                default: colorTokens.grey[900],
                alt: colorTokens.grey[800],
              },
            }
          : {
              // palette values for light mode
              primary: {
                dark: colorTokens.primary[700],
                main: colorTokens.primary[500],
                light: colorTokens.primary[50],
              },
              neutral: {
                dark: colorTokens.grey[700],
                main: colorTokens.grey[500],
                mediumMain: colorTokens.grey[400],
                medium: colorTokens.grey[300],
                light: colorTokens.grey[50],
              },
              background: {
                default: colorTokens.grey[10],
                alt: colorTokens.grey[0],
              },
            }),
      },
      typography: {
        fontFamily: ["Rubik", "sans-serif"].join(","),
        fontSize: 12,
        h1: {
          fontFamily: ["Rubik", "sans-serif"].join(","),
          fontSize: 40,
        },
        h2: {
          fontFamily: ["Rubik", "sans-serif"].join(","),
          fontSize: 32,
        },
        h3: {
          fontFamily: ["Rubik", "sans-serif"].join(","),
          fontSize: 24,
        },
        h4: {
          fontFamily: ["Rubik", "sans-serif"].join(","),
          fontSize: 20,
        },
        h5: {
          fontFamily: ["Rubik", "sans-serif"].join(","),
          fontSize: 16,
        },
        h6: {
          fontFamily: ["Rubik", "sans-serif"].join(","),
          fontSize: 14,
        },
      },
    };
  };
