import React, { useState } from "react";
import {
  ChatBubbleOutlineOutlined,
  FavoriteBorderOutlined,
  FavoriteOutlined,
  ShareOutlined,
  DeleteOutlined, // Adicionamos o ícone de exclusão
} from "@mui/icons-material";
import { Box, Divider, IconButton, Typography, useTheme } from "@mui/material";
import FlexBetween from "components/FlexBetween";
import Friend from "components/Friend";
import WidgetWrapper from "components/WidgetWrapper";
import { useDispatch, useSelector } from "react-redux";
import { setPost } from "state";

const PostWidget = ({
  postId,
  postUserId,
  name,
  description,
  location,
  picturePath,
  userPicturePath,
  likes,
  comments,
}) => {
  const [isComments, setIsComments] = useState(false);
  const dispatch = useDispatch();
  const token = useSelector((state) => state.token);
  const loggedInUserId = useSelector((state) => state.user._id);
  const isLiked = Boolean(likes[loggedInUserId]);
  const likeCount = Object.keys(likes).length;

  const { palette } = useTheme();
  const main = palette.neutral.main;
  const primary = palette.primary.main;

  const patchLike = async () => {
    // ... Código de adicionar/remover like
  };

  const deletePost = async () => {
    // Verifica se o usuário está autorizado a excluir o post
    if (loggedInUserId === postUserId) {
      const response = await fetch(`http://localhost:3001/posts/${postId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.status === 204) {
        // Post excluído com sucesso, você pode adicionar alguma lógica aqui se necessário
        // Por exemplo, redirecionar o usuário para a página inicial
      }
    }
  };

  return (
    <WidgetWrapper m="2rem 0">
      <Friend
        friendId={postUserId}
        name={name}
        subtitle={location}
        userPicturePath={userPicturePath}
      />
      {/* Botão de exclusão */}
      {loggedInUserId === postUserId && (
        <IconButton
          onClick={deletePost}
          sx={{ position: "absolute", top: "1rem", right: "1rem" }}
        >
          <DeleteOutlined sx={{ color: main }} />
        </IconButton>
      )}
      <Typography color={main} sx={{ mt: "1rem" }}>
        {description}
      </Typography>
      {/* ... Resto do código */}
    </WidgetWrapper>
  );
};

export default PostWidget;
